        "div.8": "KAT VR \u2014 \u043D\u0435\u0437\u0430\u043B\u0435\u0436\u043D\u0430 \u043A\u043E\u043C\u043F\u0430\u043D\u0456\u044F, \u043F\u0440\u0438\u0441\u0432\u044F\u0447\u0435\u043D\u0430 \u0434\u043E\u0441\u043B\u0456\u0434\u0436\u0435\u043D\u043D\u044E, \u0440\u043E\u0437\u0440\u043E\u0431\u0446\u0456 \u0442\u0430 \u043F\u0440\u043E\u0434\u0430\u0436\u0443 \u043F\u0440\u043E\u0434\u0443\u043A\u0442\u0456\u0432 VR \u043B\u043E\u043A\u043E\u043C\u043E\u0446\u0456\u0457 \u0442\u0430 \u0440\u0456\u0448\u0435\u043D\u044C. \u0417\u0430\u0441\u043D\u043E\u0432\u0430\u043D\u0430 \u0443 2013 \u0440\u043E\u0446\u0456, \u043C\u0438 \u0448\u0432\u0438\u0434\u043A\u043E \u0441\u0442\u0430\u043B\u0438 \u043E\u0434\u043D\u0438\u043C \u0456\u0437 \u043F\u0440\u043E\u0432\u0456\u0434\u043D\u0438\u0445 \u043F\u0440\u043E\u0444\u0435\u0441\u0456\u0439\u043D\u0438\u0445 \u043F\u043E\u0441\u0442\u0430\u0447\u0430\u043B\u044C\u043D\u0438\u043A\u0456\u0432 \u043E\u0431\u043B\u0430\u0434\u043D\u0430\u043D\u043D\u044F \u0434\u043B\u044F VR \u0456\u0433\u043E\u0440 \u0442\u0430 \u0441\u0438\u043C\u0443\u043B\u044F\u0446\u0456\u0439 \u0443 \u0441\u0432\u0456\u0442\u0456.",
        "h2.5": "\u0422\u0415\u0425\u041D\u0406\u0427\u041D\u0406",
        "span.14": "\u0425\u0410\u0420\u0410\u041A\u0422\u0415\u0420\u0418\u0421\u0422\u0418\u041A\u0418",
        "h3.5": "\u0421\u0415\u041D\u0421\u041E\u0420",
        "p.12": "\u0412\u0430\u0433\u0430: 35\u0433/1.23\u0443\u043D\u0446\u0456\u0457 \u043A\u043E\u0436\u0435\u043D   \n   \u0420\u043E\u0437\u043C\u0456\u0440\u0438: 50\u043C\u043C/1.97\u0434\u044E\u0439\u043C\u0430 \u0445 24\u043C\u043C/0.94\u0434\u044E\u0439\u043C\u0430   \n   \u041F\u0456\u0434\u0441\u0432\u0456\u0442\u043A\u0430: \u0441\u0432\u0456\u0442\u043B\u043E\u0434\u0456\u043E\u0434\u043D\u0430",
        "h3.6": "\u0417\u2019\u0404\u0414\u041D\u0410\u041D\u041D\u042F",
        "p.13": "\u0411\u0435\u0437\u0434\u0440\u043E\u0442\u043E\u0432\u0435: Bluetooth 4.2   \n   \u0414\u0430\u043B\u044C\u043D\u0456\u0441\u0442\u044C: 5\u043C   \n   \u041F\u0440\u0438\u0439\u043C\u0430\u0447: USB 2.0 \u0442\u0430 \u0432\u0438\u0449\u0435",
        "h3.7": "\u0410\u041A\u0423\u041C\u0423\u041B\u042F\u0422\u041E\u0420\u0418",
        "p.14": "\u0422\u0438\u043F: \u043B\u0456\u0442\u0456\u0439-\u043F\u043E\u043B\u0456\u043C\u0435\u0440\u043D\u0456 \u0431\u0430\u0442\u0430\u0440\u0435\u0457    \n  \u0404\u043C\u043D\u0456\u0441\u0442\u044C: 370\u043C\u0410\u0433   \n   \u0427\u0430\u0441 \u0440\u043E\u0431\u043E\u0442\u0438: 10 \u0433\u043E\u0434\u0438\u043D \u0431\u0435\u0437\u043F\u0435\u0440\u0435\u0440\u0432\u043D\u043E, 150 \u0433\u043E\u0434\u0438\u043D \u0443 \u0440\u0435\u0436\u0438\u043C\u0456 \u043E\u0447\u0456\u043A\u0443\u0432\u0430\u043D\u043D\u044F \n     \u0428\u0432\u0438\u0434\u043A\u0430 \u0437\u0430\u0440\u044F\u0434\u043A\u0430: 1 \u0433\u043E\u0434\u0438\u043D\u0430   \n   \u041D\u0430\u043F\u0440\u0443\u0433\u0430 \u0442\u0430 \u0441\u0442\u0440\u0443\u043C: 5\u0412 = 0.5\u0410",
        "h2.6": "\u0427\u041E\u041C\u0423",
        "span.15": "KAT LOCO?",
        "h3.8": "\u0423\u0421\u0415\u0421\u0412\u0406\u0422\u041D\u042F \u0421\u0423\u041C\u0406\u0421\u041D\u0406\u0421\u0422\u042C",
        "p.15": "KAT Loco \u043F\u0440\u043E\u043F\u043E\u043D\u0443\u0454 \u0443\u043D\u0456\u0432\u0435\u0440\u0441\u0430\u043B\u044C\u043D\u0443 \u0441\u0443\u043C\u0456\u0441\u043D\u0456\u0441\u0442\u044C \u0437 \u0443\u0441\u0456\u043C\u0430 \u043E\u0441\u043D\u043E\u0432\u043D\u0438\u043C\u0438 VR \u0433\u0430\u0440\u043D\u0456\u0442\u0443\u0440\u0430\u043C\u0438 \u0442\u0430",
        "span.16": "\u043F\u043B\u0430\u0442\u0444\u043E\u0440\u043C\u0430\u043C\u0438...",
        "span.17": "...",
        "span.18": "\u043F\u043B\u0430\u0442\u0444\u043E\u0440\u043C\u0430\u043C\u0438, \u0449\u043E \u0434\u043E\u0437\u0432\u043E\u043B\u044F\u044E\u0442\u044C \u0433\u0440\u0430\u0442\u0438 \u0443 \u0431\u0443\u0434\u044C-\u044F\u043A\u0456 VR \u0456\u0433\u0440\u0438 \u0437 \u043F\u0456\u0434\u0442\u0440\u0438\u043C\u043A\u043E\u044E   \u0432\u0456\u043B\u044C\u043D\u043E\u0457 \u043B\u043E\u043A\u043E\u043C\u043E\u0446\u0456\u0457",
        "h3.9": "\u041F\u0410\u041D\u0415\u041B\u042C \u041A\u0415\u0420\u0423\u0412\u0410\u041D\u041D\u042F VR/PC",
        "p.16": "\u041D\u0430\u0448\u0435 \u0431\u0430\u0433\u0430\u0442\u043E\u0444\u0443\u043D\u043A\u0446\u0456\u043E\u043D\u0430\u043B\u044C\u043D\u0435 \u043F\u0440\u043E\u0433\u0440\u0430\u043C\u043D\u0435 \u0437\u0430\u0431\u0435\u0437\u043F\u0435\u0447\u0435\u043D\u043D\u044F \u0434\u043E\u0437\u0432\u043E\u043B\u044F\u0454 \u0448\u0432\u0438\u0434\u043A\u043E   \u0434\u043E\u0441\u0442\u0443\u043F\u0430\u0442\u0438 \u0434\u043E \u043F\u0430\u043D\u0435\u043B\u0456 \u043A\u0435\u0440\u0443\u0432\u0430\u043D\u043D\u044F KAT Loco",
        "span.19": "\u044F\u043A \u0437 \u0440\u043E\u0431\u043E\u0447\u043E\u0433\u043E \u0441\u0442\u043E\u043B\u0443...",
        "span.20": "...",
        "span.21": "\u044F\u043A \u0437 \u0440\u043E\u0431\u043E\u0447\u043E\u0433\u043E \u0441\u0442\u043E\u043B\u0443, \u0442\u0430\u043A \u0456 \u0431\u0435\u0437\u043F\u043E\u0441\u0435\u0440\u0435\u0434\u043D\u044C\u043E \u0437 \u0432\u0430\u0448\u043E\u0457 VR \u0433\u0430\u0440\u043D\u0456\u0442\u0443\u0440\u0438.",
        "h3.10": "\u0411\u0415\u0417\u0414\u0420\u041E\u0422\u041E\u0412\u0406 \u0421\u0415\u041D\u0421\u041E\u0420\u0418",
        "p.17": "\u0429\u0435 \u0431\u0456\u043B\u044C\u0448 \u043F\u0440\u043E\u0441\u0443\u043D\u0443\u0442\u043E, KAT Loco \u043F\u043E\u0432\u043D\u0456\u0441\u0442\u044E \u0431\u0435\u0437\u0434\u0440\u043E\u0442\u043E\u0432\u0438\u0439, \u0442\u0430",
        "span.22": "\u043F\u043E\u0441\u0442\u0430\u0432\u043B\u044F\u0454\u0442\u044C\u0441\u044F \u0437\u2026",
        "span.23": "...",
        "span.24": "\u043F\u043E\u0441\u0442\u0430\u0432\u043B\u044F\u0454\u0442\u044C\u0441\u044F \u0437 \u043F\u043E\u0432\u043D\u043E\u044E \u0441\u0438\u0441\u0442\u0435\u043C\u043E\u044E \u0430\u043B\u0433\u043E\u0440\u0438\u0442\u043C\u0456\u0432, \u043D\u0430\u0434\u043C\u0456\u0446\u043D\u0438\u043C\u0438 \u0431\u0430\u0442\u0430\u0440\u0435\u044F\u043C\u0438 \u0456 \u0431\u0430\u0433\u0430\u0442\u043E \u0447\u0438\u043C \u0456\u043D\u0448\u0438\u043C!",
        "button.5": "\u041A\u0443\u043F\u0438\u0442\u0438 \u0437\u0430\u0440\u0430\u0437",
        "p.18": "\u0404 \u043F\u0438\u0442\u0430\u043D\u043D\u044F?",
